import React, { useContext } from 'react';
import RecipesAppContext from '../context/RecipesAppContext';
import { getIngredient, getId } from '../services/Functions';

export default function DetalhesComidas() {
  const { idDishes } = useContext(RecipesAppContext);
  return (
    <div>
      {
        idDishes.map((dishe) => (
          <div key={ dishe.idMeal }>
            <img
              data-testid="recipe-photo"
              src={ `${dishe.strMealThumb}` }
              alt=""
              width="400"
            />
            <p data-testid="recipe-title">{dishe.strMeal}</p>
            <p data-testid="recipe-category">{dishe.strCategory}</p>
            <p data-testid="instructions">{dishe.strInstructions}</p>
            <ul>
              { getIngredient(dishe).map((ingrediente) => (
                <li
                  key={ ingrediente.nome }
                  data-testid={ `${ingrediente.index}-ingredient-name-and-measure` }
                >
                  {ingrediente.nome}
                  -
                  {ingrediente.quantidade}
                </li>
              ))}
            </ul>
            <iframe
              data-testid="video"
              width="560"
              height="315"
              title="video"
              src={ `//www.youtube.com/embed/${getId(dishe.strYoutube)}` }
              frameBorder="0"
              allowFullScreen
            />
            <input
              type="button"
              data-testid="share-btn"
              value="Compartilhar"
            />
            <input
              type="button"
              data-testid="favorite-btn"
              value="Favoritar"
            />
            <input
              type="button"
              data-testid="start-recipe-btn"
              value="Iniciar Receita"
            />
          </div>
        ))
      }
    </div>
  );
}
