import React, { useContext, useState } from 'react';
import { useHistory } from 'react-router-dom';
import RecipesAppContext from '../context/RecipesAppContext';
import profileIcon from '../images/profileIcon.svg';
import searchIcon from '../images/searchIcon.svg';

function Header() {
  const [toggle, setToggle] = useState(false);
  const [radioName, setRadioName] = useState({ radio: '', text: '' });
  const { setParameters } = useContext(RecipesAppContext);
  const history = useHistory();

  const setToggleInput = () => {
    setToggle(!toggle);
  };

  function handleClick() {
    setParameters(radioName);
  }

  return (
    <header>
      <h1 data-testId="page-title">Receitas</h1>

      <button
        type="button"
        onClick={ () => history.push('/perfil') }
        data-testId="profile-top-btn"
      >
        <img src={ profileIcon } alt="Profile Icon" />
      </button>

      <button
        type="button"
        onClick={ setToggleInput }
        data-testId="search-top-btn"
      >
        <img src={ searchIcon } alt="Profile Icon" />
      </button>
      { toggle && (<input type="text"  onChange={ (e) => setRadioName({ ...radio, text: e.target.value }) } />)}
      <hr />
      <label htmlFor="ingredient-search-radio">
        <input
          type="radio"
          data-testid="ingredient-search-radio"
          onChange={ (e) => setRadioName({ ...text, radio: e.target.value }) }
          name="method"
        />
        Ingrediente
      </label>
      <label htmlFor="name-search-radio">
        <input
          type="radio"
          data-testid="name-search-radio"
          onChange={ (e) => setRadioName({ ...text, radio: e.target.value }) }
          name="method"
        />
        Nome
      </label>
      <label htmlFor="first-letter-search-radio">
        <input
          type="radio"
          data-testid="first-letter-search-radio"
          value="primeira-letra"
          onChange={ (e) => setRadioName({ ...text, radio: e.target.value }) }
          name="method"
        />
        Primeira letra
      </label>
      <input
        type="button"
        data-testid="exec-search-btn"
        name="exec-btn"
        onClick={ () => handleClick() }
        value="Pesquisar"
      />
    </header>
  );
}

export default Header;
