import React, { useContext } from 'react';
import { useParams } from 'react-router';
import RecipesAppContext from '../context/RecipesAppContext';
import { getIngredient, getId } from '../services/Functions';

export default function DetalhesComidas() {
  const { idDishes, drinks, setFoodId } = useContext(RecipesAppContext);
  const { id } = useParams();
  const MAX_LENGTH_ARRAY = 6;
  const style = {
    display: 'flex',
    justifyContent: 'space-between',
    background: 'white',
    position: 'fixed',
    bottom: '0',
    height: '30px',
    width: '10%',
  };
  const sixDrinks = json.drinks.slice(0, MAX_LENGTH_ARRAY);
  setFoodId(id);
  return (
    <div>
      {
        idDishes.map((dishe) => (
          <div key={ dishe.idMeal }>
            <img
              data-testid="recipe-photo"
              src={ `${dishe.strMealThumb}` }
              alt=""
              width="400"
            />
            <p data-testid="recipe-title">{dishe.strMeal}</p>
            <p data-testid="recipe-category">{dishe.strCategory}</p>
            <p data-testid="instructions">{dishe.strInstructions}</p>
            <ul>
              { getIngredient(dishe).map((ingrediente, index) => (
                <li
                  key={ ingrediente.nome }
                  data-testid={ `${index}-ingredient-name-and-measure` }
                >
                  {ingrediente.nome}
                  -
                  {ingrediente.quantidade}
                </li>
              ))}
            </ul>
            <iframe
              data-testid="video"
              width="560"
              height="315"
              title="video"
              src={ `//www.youtube.com/embed/${getId(dishe.strYoutube)}` }
              frameBorder="0"
              allowFullScreen
            />
            { drinks.map((drinksRecomendations, index) => (
              <p
                key={ `${index}-key` }
                data-testid={ `${index}-recomendation-card` }
              >
                { drinksRecomendations.strDrink }
              </p>
            )) }
            <input
              type="button"
              data-testid="share-btn"
              value="Compartilhar"
            />
            <input
              type="button"
              data-testid="favorite-btn"
              value="Favoritar"
            />
            <input
              type="button"
              data-testid="start-recipe-btn"
              style={ style }
              value="Iniciar Receita"
            />
          </div>
        ))
      }
    </div>
  );
}
