import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import RecipesAppContext from './RecipesAppContext';
import RequisitionFood, {
  RequisitionFoodFirstName, RequisitionFoodName } from '../services/Requisitions';

function RecipesAppProvider({ children }) {
  const [parameters, setParameters] = useState({ radio: '', text: '' });
  const [dishes, setDishes] = useState([]);
  console.log(parameters.text)
  useEffect(() => {
    if (parameters.text === '') {
      RequisitionFood('').then((response) => setDishes(response));
    }
    if (parameters.radio === 'ingrediente') {
      RequisitionFood(parameters.text).then((response) => setDishes(response));
    }
    if (parameters.radio === 'nome') {
      RequisitionFoodName(parameters.text).then((response) => setDishes(response));
    }
    if (parameters.radio === 'primeira-letra' && parameters.text.length > 1) {
      global.alert('Sua busca deve conter somente 1 (um) caracter');
    } else if (parameters.radio === 'primeira-letra' && parameters.text !== '') {
      RequisitionFoodFirstName(parameters.text).then((response) => setDishes(response));
    }
  }, [parameters.radio, parameters.text]);

  return (
    <RecipesAppContext.Provider value={ { parameters, setParameters, dishes } }>
      { children }
    </RecipesAppContext.Provider>
  );
}

RecipesAppProvider.propTypes = {
  children: PropTypes.node.isRequired,
};

export default RecipesAppProvider;
