import React, { useContext } from 'react';
import RecipesAppContext from '../context/RecipesAppContext';

export default function DetalhesComidas() {
  const { idDishes } = useContext(RecipesAppContext);
  function getId(url) {
    const regExp = /^.*(youtu.be\/|v\/|u\/\w\/|embed\/|watch\?v=|&v=)([^#&?]*).*/;
    const match = url.match(regExp);

    return (match && match[2].length === 11)
      ? match[2]
      : null;
}

  return (
    <div>
      {
        idDishes.map((dishe) => (
          <div key={ dishe.idMeal }>
            <img
              data-testid="recipe-photo"
              src={ `${dishe.strMealThumb}` }
              alt=""
              width="400"
            />
            <p data-testid="recipe-title">{dishe.strMeal}</p>
            <p data-testid="recipe-category">{dishe.strCategory}</p>
            <p data-testid="instructions">{dishe.strInstructions}</p>
            <iframe
              width="560" height="315" title="video" src={ `//www.youtube.com/embed/${getId(dishe.strYoutube)}` } frameBorder="0" allowFullScreen />
            <input
              type="button"
              data-testid="share-btn"
              value="Compartilhar"
            />
            <input
              type="button"
              data-testid="favorite-btn"
              value="Favoritar"
            />
          </div>
        ))
      }
    </div>
  );
}
