import React, { useContext } from 'react';
import { useHistory } from 'react-router-dom';
import Footer from '../components/Footer';
import Header from '../components/Header';

import RecipesAppContext from '../context/RecipesAppContext';

export default function DetalhesComidas() {
  const { idDishes } = useContext(RecipesAppContext);
  const history = useHistory();

  return (
    <div data-testid="comida">
      <Header />
      <div>
        {
          idDishes
            ? idDishes.map((dishe) => (
              <card
                key={ dishe.idMeal }
              >
                <p>{dishe.strMeal}</p>
                <img src={ dishe.strMealThumb } alt={ dishe.strMeal } width="150" />
              </card>))
            : <div> NÃ£o encontramos resultados pra sua pesquisa</div>
        }
      </div>
      <Footer />
    </div>
  );
}
