import React, { useContext } from 'react';
import { useParams } from 'react-router';
import RecipesAppContext from '../context/RecipesAppContext';
import { getIngredient } from '../services/Functions';

export default function DetalhesBebidas() {
  const { drinks, idDrinks } = useContext(RecipesAppContext);
  const { id } = useParams();
   useEffect(() => {
    getDrinksById(drinkId).then((response) => setIdDrinks(response));
  }, [drinkId]);

  return (
    <div>
      {
        idDrinks.map((drink) => (
          <div key={ drink.idDrink }>
            <img
              data-testid="recipe-photo"
              src={ `${drink.strDrinkThumb}` }
              alt=""
              width="400"
            />
            <p data-testid="recipe-title">{drink.strDrink}</p>
            <p data-testid="recipe-category">{drink.strCategory}</p>
            <p data-testid="instructions">{drink.strInstructions}</p>
            <ul>
              { getIngredient(drink).map((ingrediente, index) => (
                <li
                  key={ ingrediente.nome }
                  data-testid={ `${index}-ingredient-name-and-measure` }
                >
                  {ingrediente.nome}
                  -
                  {ingrediente.quantidade}
                </li>
              ))}
            </ul>
            { drinks.map((drinksRecomendations, index) => (
              <p
                key={ `${index}-key` }
                data-testid={ `${index}-recomendation-card` }
              >
                { drinksRecomendations.strDrink }
              </p>
            )) }

            <input
              type="button"
              data-testid="share-btn"
              value="Compartilhar"
            />
            <input
              type="button"
              data-testid="favorite-btn"
              value="Favoritar"
            />
            <input
              type="button"
              data-testid="start-recipe-btn"
              value="Iniciar Receita"
            />
          </div>
        ))
      }
    </div>
  );
}
