import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import RecipesAppContext from './RecipesAppContext';
import RequisitionFood, {
  RequisitionFoodFirstName,
  RequisitionFoodName, getFoodByArea,
  RequisitionIdFood,
} from '../services/RequestDishes';
import RequisitionDrink,
{ getDrinksByIngredient, getDrinksById } from '../services/RequestDrinks';

function RecipesAppProvider({ children }) {
  const [dishesResquest, setDishesRequest] = useState({ type: '', description: '' });
  const [drinksResquest, setDrinksRequest] = useState({ type: '', description: '' });
  const [foodId, setFoodId] = useState();
  const [drinkId, setDrinkId] = useState();
  const [idDishes, setIdDishes] = useState([]);
  const [idDrinks, setIdDrinks] = useState([]);
  const [dishes, setDishes] = useState([]);
  const [suggestionDishes, setSuggestionDishes] = useState([]);
  const [drinks, setDrinks] = useState([]);
  const firstLetter = 'primeira-letra';

  useEffect(() => {
    if (dishesResquest.description === '') {
      RequisitionFood('').then((response) => setDishes(response));
    }
    if (dishesResquest.type === 'ingrediente') {
      RequisitionFood(dishesResquest.description).then((response) => setDishes(response));
    }
    if (dishesResquest.description === 'area') {
      getFoodByArea(dishesResquest.type).then((response) => setDishes(response));
    }
    if (dishesResquest.type === 'nome') {
      RequisitionFoodName(dishesResquest.description)
        .then((response) => setDishes(response));
    }
    if (dishesResquest.type === firstLetter
      && dishesResquest.description.length > 1) {
      global.alert('Sua busca deve conter somente 1 (um) caracter');
    } else if (dishesResquest.type === firstLetter && dishesResquest.description) {
      RequisitionFoodFirstName(dishesResquest.description)
        .then((response) => setDishes(response));
    }
  }, [dishesResquest.type, dishesResquest.description]);

  useEffect(() => {
    const { description, type } = drinksResquest;
    if (description === '') {
      RequisitionDrink(description, 's').then((response) => setDrinks(response));
    }
    if (type === 'nome') {
      RequisitionDrink(description, 's').then((response) => setDrinks(response));
    }
    if (type === firstLetter
       && description.length > 1) {
      global.alert('Sua busca deve conter somente 1 (um) caracter');
    } else if (type === firstLetter
        && description) {
      RequisitionDrink(description, 'f').then((response) => setDrinks(response));
    }
    if (type === 'ingrediente') {
      getDrinksByIngredient(description).then((response) => setDrinks(response));
    }
  }, [drinksResquest]);

  useEffect(() => {
    RequisitionIdFood(foodId).then((response) => setIdDishes(response));
  }, [foodId]);

  useEffect(() => {
    getDrinksById(drinkId).then((response) => setIdDrinks(response));
    RequisitionFoodName('')
      .then((response) => setSuggestionDishes(response));
  }, [drinkId]);
