    <RecipesAppContext.Provider
      value={ {
        dishesResquest,
        setDishesRequest,
        drinksResquest,
        setDrinksRequest,
        dishes,
        drinks,
        setFoodId,
        setDrinkId,
        idDishes,
        idDrinks,
        suggestionDishes } }
    >
      { children }
    </RecipesAppContext.Provider>
  );
}

RecipesAppProvider.propTypes = {
  children: PropTypes.node.isRequired,
};

export default RecipesAppProvider;
