import React, { useContext } from 'react';
import { Link } from 'react-router-dom';
import Footer from '../components/Footer';
import Header from '../components/Header';
import { getDrinkIngredientList, getDrinksByIngredient } from '../services/RequestDrinks';
import RecipesAppContext from '../context/RecipesAppContext';
import Categories from '../components/Categories';

export default function Bebidas() {
  const { drinks, setDrinksRequest,
    setDrinks, toggle } = useContext(RecipesAppContext);
  return (
    <div data-testid="bebida">
      <Header request={ setDrinksRequest } />
      { !toggle
      && <Categories
        getCategories={ getDrinkIngredientList }
        getByCategorie={ getDrinksByIngredient }
        setRequest={ setDrinksRequest }
        keyRequest={ { type: '', description: '' } }
        setElements={ setDrinks }
      />}
      <div>
        {
          drinks
            && drinks.map((drink, index) => (
              <Link
                to={ `bebidas/${drink.idDrink}` }
                style={ { display: 'block' } }
                data-testid={ `${index}-recipe-card` }
                key={ drink.idDrink }
              >
                <p data-testid={ `${index}-card-name` }>{drink.strDrink}</p>
              { drinks.length === 1 && history.push(`/comidas/${drinks[0].idDrink}`)}
                <img
                  data-testid={ `${index}-card-img` }
                  src={ drink.strDrinkThumb }
                  alt={ drink.strDrink }
                  width="150"
                />
              </Link>))
        }
      </div>
      <Footer />
    </div>
  );
}
