import React, { useContext, useEffect } from 'react';
import countries from '../data';
import RecipesAppContext from '../context/RecipesAppContext';
import Footer from '../components/Footer';

function ExplorarComidasOrigem() {
  const { setParameters, dishes } = useContext(RecipesAppContext);

  useEffect(() => { setParameters({ radio: '', text: '' }); }, [setParameters]);

  return (
    <>
      <h1>Comidas Origem</h1>
      <label htmlFor="countries">
        <select
          data-testid="explore-by-area-dropdown"
          id="countries"
          onChange={ (e) => {
            console.log(e.target.value);
            setParameters({ radio: e.target.value, text: 'area' });
          } }
        >
          { countries.map((name) => (
            <option
              data-testid={ `${name}-option` }
              value={ name }
              key={ name }
            >
              {name}
            </option>
          ))}
        </select>
      </label>
      <div>
        {
          dishes
            ? dishes.map((dishe, index) => (
              <div key={ dishe.idMeal } data-testid={ `${index}-recipe-card` }>
                <p data-testid={ `${index}-card-name` }>{dishe.strMeal}</p>
                <img
                  src={ dishe.strMealThumb }
                  alt={ dishe.strMeal }
                  width="150"
                  data-testid={ `${index}-card-img` }
                />
              </div>))
            : <div> NÃ£o encontramos resultados pra sua pesquisa</div>
        }
      </div>
      <Footer />
    </>
  );
}

export default ExplorarComidasOrigem;
