import React, { useContext } from 'react';
import { useParams } from 'react-router';
import RecipesAppContext from '../context/RecipesAppContext';
import { getIngredient } from '../services/Functions';

export default function DetalhesBebidas() {
  const { idDrinks,
    setDrinkId,
    suggestionDishes,
    drinksResquest
  } = useContext(RecipesAppContext);
  const { id } = useParams();
  const MAX_LENGTH_ARRAY = 6;
  const style = {
    display: 'flex',
    justifyContent: 'space-between',
    background: 'white',
    position: 'fixed',
    bottom: '0',
    height: '30px',
    width: '10%',
  };
  const sixDishes = suggestionDishes && suggestionDishes.slice(0, MAX_LENGTH_ARRAY);
  setDrinkId(id);
  drinksResquest();
  return (
    <div>
      { idDrinks
        && idDrinks.map((drink) => (
          <div key={ drink.idDrink }>
            <img
              data-testid="recipe-photo"
              src={ `${drink.strDrinkThumb}` }
              alt=""
              width="400"
            />
            <p data-testid="recipe-title">{drink.strDrink}</p>
            <p>{drink.strAlcoholic}</p>
            <p data-testid="recipe-category">
              {drink.strCategory}
              {drink.strAlcoholic}
            </p>
            <p data-testid="instructions">{drink.strInstructions}</p>
            <ul>
              { getIngredient(drink).map((ingrediente, index) => (
                <li
                  key={ ingrediente.nome }
                  data-testid={ `${index}-ingredient-name-and-measure` }
                >
                  {ingrediente.nome}
                  -
                  {ingrediente.quantidade}
                </li>
              ))}
            </ul>
            { sixDishes.map((MealRecomendation, index) => (
              <p
                key={ `${index}-key` }
                data-testid={ `${index}-recomendation-card` }
              >
                { MealRecomendation.strMeal}
              </p>
            )) }
            <input
              type="button"
              data-testid="share-btn"
              value="Compartilhar"
            />
            <input
              type="button"
              data-testid="favorite-btn"
              value="Favoritar"
            />
            <input
              type="button"
              style={ style }
              data-testid="start-recipe-btn"
              value="Iniciar Receita"
            />
          </div>
        ))}
    </div>
  );
}
