export default async function RequisitionDrink(drink) {
  const MAX_LENGTH_ARRAY = 12;

  const url = `https://www.thecocktaildb.com/api/json/v1/1/search.php?s=${drink}`;
  const responseApi = await fetch(url);
  const json = await responseApi.json();

  const drinks = json.drinks.slice(0, MAX_LENGTH_ARRAY);

  return drinks;
}

export async function getDrinksIngredients() {
  const MAX_LENGTH_ARRAY = 12;
  const url = 'https://www.thecocktaildb.com/api/json/v1/1/search.php?s=';
  const response = await fetch(url);
  const json = await response.json();

  const allIngredients = json.drinks.slice(0, MAX_LENGTH_ARRAY);

  return allIngredients;
}

export async function getDrinksById(id) {
  const MAX_LENGTH_ARRAY = 12;
  const url = `https://www.thecocktaildb.com/api/json/v1/1/lookup.php?i=${id}`;
  const response = await fetch(url);
  const json = await response.json();

  const allIngredients = json.drinks.slice(0, MAX_LENGTH_ARRAY);

  return allIngredients;
}
