import React, { useContext } from 'react';
import RecipesAppContext from '../context/RecipesAppContext';

export default function DetalhesComidas() {
  const { idDishes } = useContext(RecipesAppContext);
  return (
    <div>
      {
        idDishes.map((dishe) => (
          <div key={ dishe.idMeal }>
            <img
              data-testid="recipe-photo"
              src={ `${dishe.strMealThumb}` }
              alt=""
              width="400"
            />
            <p data-testid="recipe-title">{dishe.strMeal}</p>
            <p data-testid="recipe-category">{dishe.strCategory}</p>
            <p data-testid="instructions">{dishe.strInstructions}</p>
            <object width="425" height="350">
              <param name="movie" value={ dishe.strYoutube } />
              <embed
                src={ dishe.strYoutube }
                type="application/x-shockwave-flash"
                width="425"
                height="350"
              />
            </object>
          </div>
        ))
      }
    </div>
  );
}
