import React, { useContext } from 'react';
import { useHistory } from 'react-router-dom';
import Footer from '../components/Footer';
import Header from '../components/Header';

import RecipesAppContext from '../context/RecipesAppContext';

export default function Comidas() {
  const { dishes, setIngredientId, setDishesRequest } = useContext(RecipesAppContext);
  const history = useHistory();

  return (
    <div data-testid="comida">
      <h1 data-testid="page-title">Comidas</h1>
      <Header request={ setDishesRequest } />
      <div>
        {
          dishes
            ? dishes.map((dishe, index) => (
              <card
                data-testid={ `${index}-recipe-card` }
                key={ dishe.idMeal }
                onClick={ () => (
                  history.push(`comidas/${dishe.idMeal}`,
                    setIngredientId(dishe.idMeal))
                ) }
              >
                <p data-testid={ `${index}-card-name` }>{dishe.strMeal}</p>
                <img
                  data-testid={ `${index}-card-img` }
                  src={ dishe.strMealThumb }
                  alt={ dishe.strMeal }
                  width="150"
                />
              </card>))
            : global
              .alert('Sinto muito, não encontramos nenhuma receita para esses filtros.')
        }
      </div>
      <Footer />
    </div>
  );
}
