export default async function RequisitionDrink(drink, letter) {
  const MAX_LENGTH_ARRAY = 12;

  const url = `https://www.thecocktaildb.com/api/json/v1/1/search.php?${letter}=${drink}`;
  const responseApi = await fetch(url);
  const json = await responseApi.json();

  const drinks = json.drinks.length > 0 && json.drinks.slice(0, MAX_LENGTH_ARRAY);

  return drinks;
}

export async function getDrinkIngredientList() {
  const MAX_LENGTH_ARRAY = 12;
  const url = 'https://www.thecocktaildb.com/api/json/v1/1/list.php?i=list';
  const response = await fetch(url);
  const json = await response.json();

  const allIngredients = json.drinks.slice(0, MAX_LENGTH_ARRAY);

  return allIngredients;
}

export async function getDrinksByIngredient(ingredient) {
  const MAX_LENGTH_ARRAY = 12;
  const url = `https://www.thecocktaildb.com/api/json/v1/1/filter.php?i=${ingredient}`;
  const response = await fetch(url);
  const json = await response.json();

  const allIngredients = json.drinks.slice(0, MAX_LENGTH_ARRAY);

  return allIngredients;
}

export async function getDrinksById(id) {
  const url = `https://www.thecocktaildb.com/api/json/v1/1/lookup.php?i=${id}`;
  const response = await fetch(url);
  const json = await response.json();

  return json.drinks;
}
