import React, { useContext } from 'react';
import { useHistory } from 'react-router-dom';
import Footer from '../components/Footer';
import Header from '../components/Header';

import RecipesAppContext from '../context/RecipesAppContext';

export default function Comidas() {
  const { dishes, setIngredientId } = useContext(RecipesAppContext);
  const history = useHistory();

  return (
    <div data-testid="comida">
      <Header />
      <div>
        {
          dishes
            ? dishes.map((dishe, index) => (
              <card
                data-testid={ `${index}-recipe-card` }
                key={ dishe.idMeal }
                onClick={ () => (
                  history.push(`comidas/${dishe.idMeal}`,
                    setIngredientId(dishe.idMeal))
                ) }
              >
                <p>{dishe.strMeal}</p>
                <img src={ dishe.strMealThumb } alt={ dishe.strMeal } width="150" />
              </card>))
            : <div> NÃ£o encontramos resultados pra sua pesquisa</div>
        }
      </div>
      <Footer />
    </div>
  );
}
