import React, { useContext } from 'react';
import RecipesAppContext from '../context/RecipesAppContext';

export default function DetalhesComidas() {
  const { idDishes } = useContext(RecipesAppContext);
  console.log(idDishes);
  return (
    <div>
      {
        idDishes.map((dishe) => (
          <div key={ dishe.idMeal }>
            <img
              data-testid="recipe-photo"
              src={ `${dishe.strMealThumb}` }
              alt=""
              width="400"
            />
            <p data-testid="recipe-title">{dishe.strMeal}</p>
            <p data-testid="recipe-category">{dishe.strMeal}</p>
            <p data-testid="instructions">{dishe.strMeal}</p>
            <video data-testid="video" width="320" height="240" controls>
              <source src={ dishe.strYoutube } type="video/mp4" />
            </video>
          </div>
        ))
      }
    </div>
  );
}
