import React, { useContext } from 'react';
import { useHistory } from 'react-router-dom';
import Footer from '../components/Footer';
import Header from '../components/Header';

import RecipesAppContext from '../context/RecipesAppContext';

export default function Bebidas() {
  const { drinks, setDrinksRequest, setDrinkId } = useContext(RecipesAppContext);
  const history = useHistory();
  return (
    <div data-testid="bebida">
      <Header request={ setDrinksRequest } />
      <div>
        {
          drinks
            ? drinks.map((drink, index) => (
              <card
                data-testid={ `${index}-recipe-card` }
                key={ drink.idDrink }
                onClick={ () => (
                  history.push(`bebidas/${drink.idDrink}`,
                    setDrinkId(drink.idDrink))
                ) }
              >
                <p data-testid={ `${index}-card-name` }>{drink.strDrink}</p>
                <img
                data-testid={ `${index}-card-img` }
                src={ drink.strDrinkThumb } alt={ drink.strDrink } width="150" />
              </card>))
            : global
              .alert('Sinto muito, não encontramos nenhuma receita para esses filtros.')
        }
      </div>
      <Footer />
    </div>
  );
}
