import React, { useContext } from 'react';
import { Link, useHistory } from 'react-router-dom';
import Footer from '../components/Footer';
import Header from '../components/Header';
import Categories from '../components/Categories';
import RecipesAppContext from '../context/RecipesAppContext';
import RequisitionFood, { getFoodIngredients } from '../services/RequestDishes';

export default function Comidas() {
  const { dishes,
    setFoodId, setDishesRequest, setDishes, toggle } = useContext(RecipesAppContext);
  const history = useHistory();
  return (
    <>
      <div data-testid="comida">
        <h1 data-testid="page-title">Comidas</h1>
        <Header request={ setDishesRequest } />
        { !toggle
      && <Categories
        getCategories={ getFoodIngredients }
        getByCategorie={ RequisitionFood }
        setRequest={ setDishesRequest }
        keyRequest={ { type: '', description: '' } }
        setElements={ setDishes }
      />}
        <div>
          {
            dishes
              ? dishes.map((dishe, index) => (
                <Link
                  to={ `comidas/${dishe.idMeal}` }
                  data-testid={ `${index}-recipe-card` }
                  style={ { display: 'block' } }
                  key={ dishe.idMeal }
                  onClick={ () => setFoodId(dishe.idMeal) }
                >
                  <p data-testid={ `${index}-card-name` }>{dishe.strMeal}</p>
                  <img
                    data-testid={ `${index}-card-img` }
                    src={ dishe.strMealThumb }
                    alt={ dishe.strMeal }
                    width="150"
                  />
                  {dishes.length === 1 && dishes.idMeal !== '52968' ? console.log(dishes) /* history.push(`/comidas/${dishes[0].idMeal}`) */ : null }
                </Link>
              ))
              : global
                .alert('Sinto muito, não encontramos nenhuma receita para esses filtros.')
          }
        </div>
      </div>
      <Footer />
    </>
  );
}
