import React, { useState, useContext } from 'react';
import PropTypes from 'prop-types';
import { useHistory } from 'react-router-dom';
import profileIcon from '../images/profileIcon.svg';
import searchIcon from '../images/searchIcon.svg';
import RecipesAppContext from '../context/RecipesAppContext';

function Header({ request }) {
  const { toggle, setToggle } = useContext(RecipesAppContext);
  const [values, setValues] = useState({ description: '' });
  const history = useHistory();

  function setToggleInput() {
    setToggle(!toggle);
  }

  return (
    <header>
      <h1 data-testid="page-title">Receitas</h1>
      <button
        type="button"
        onClick={ () => history.push('/perfil') }
        data-testid="profile-top-btn"
      >
        <img src={ profileIcon } alt="Profile Icon" />
      </button>

      <button
        type="button"
        onClick={ setToggleInput }
        data-testid="search-top-btn"
      >
        <img src={ searchIcon } alt="Profile Icon" />
      </button>
      { toggle && (
        <>
          <input
            type="text"
            data-testid="search-input"
            onChange={ (e) => setValues({ ...values, description: e.target.value }) }
          />
          <hr />

          <form
            onSubmit={ (event) => {
              event.preventDefault();
              request(values);
            } }
          >
            <label htmlFor="ingredient-search-radio">
              <input
                id="ingredient-search-radio"
                type="radio"
                style={ { display: 'block' } }
                data-testid="ingredient-search-radio"
                onChange={ (e) => setValues({ ...values, type: e.target.value }) }
                name="method"
                value="ingrediente"
              />
              Ingrediente
            </label>
            <label htmlFor="name-search-radio">
              <input
                id="name-search-radio"
                type="radio"
                style={ { display: 'block' } }
                data-testid="name-search-radio"
                onChange={ (e) => setValues({ ...values, type: e.target.value }) }
                name="method"
                value="nome"
              />
              Nome
            </label>
            <label htmlFor="first-letter-search-radio">
              <input
                id="first-letter-search-radio"
                type="radio"
                style={ { display: 'block' } }
                data-testid="first-letter-search-radio"
                onChange={ (e) => setValues({ ...values, type: e.target.value }) }
                name="method"
                value="primeira-letra"
              />
              Primeira letra
            </label>
            <button
              type="submit"
              data-testid="exec-search-btn"
              style={ { display: 'block' } }
              name="exec-btn"
              value="Pesquisar"
            >
              Pesquisar
            </button>
          </form>
        </>
      )}
    </header>
  );
}

Header.propTypes = {
  request: PropTypes.func.isRequired,
};

export default Header;
