import React, { useEffect, useState, useContext } from 'react';
import { useHistory } from 'react-router-dom';
import { getFoodIngredients } from '../services/Requisitions';
import RecipesAppContext from '../context/RecipesAppContext';

function ExplorarComidasIngredientes() {
  const [ingredients, setIngredients] = useState([]);
  const { setParameters } = useContext(RecipesAppContext);

  const history = useHistory();

  useEffect(() => {
    getFoodIngredients().then((data) => setIngredients(data));
  }, []);

  return (
    <>
      <h1>Comidas Ingredientes</h1>
      { ingredients.map(({ strIngredient }, index) => (
        <button
          type="button"
          data-testid={ `${index}-ingredient-card` }
          key={ strIngredient }
          onClick={ () => {
            history.push('/comidas');
            setParameters({ text: strIngredient, radio: 'area' });
          } }
        >
          <img src={ `https://www.themealdb.com/images/ingredients/${strIngredient}-Small.png` } alt="" data-testid={ `${index}-card-img` } />
          <p data-testid={ `${index}-card-name` }>{ strIngredient }</p>
        </button>
      ))}
    </>
  );
}

export default ExplorarComidasIngredientes;
