import React, { useState, useEffect, useContext } from 'react';
import Header from '../components/Header';
import RequisitionFood, { RequisitionFoodFirstName,  } from '../services/Requisitions';
import RecipesAppContext from '../context/RecipesAppContext';

export default function Comidas() {
  const { parameters } = useContext(RecipesAppContext);
  console.log(parameters);
  const [resultado, incResult] = useState([]);
  useEffect(() => {
    RequisitionFood('').then((response) => incResult(response));
  }, []);

  useEffect(() => {
    if (parameters.radio === 'ingrediente') {
      RequisitionFood(parameters.text).then((response) => incResult(response));
    }
    if (parameters.radio === 'nome') {
      RequisitionFoodName(parameters.text).then((response) => incResult(response));
    }
    if (parameters.radio === 'primeira-letra') {
      RequisitionFoodFirstName(parameters.text).then((response) => incResult(response));
    }
  });

  function returnFood(food) {
    return (
      <div key={ food.idMeal }>
        <p>{food.strMeal}</p>
        <img src={ food.strMealThumb } alt={ food.strMeal } width="150" />
      </div>
    );
  }

  return (
    <div data-testid="comida">
      <Header />
      <div>
        {
          resultado && resultado.map((elem) => returnFood(elem))
        }
      </div>
    </div>
  );
}
