import React, { useState, useEffect } from 'react';
import PropTypes from 'prop-types';
import RecipesAppContext from './RecipesAppContext';
import RequisitionFood, {
  RequisitionFoodFirstName,
  RequisitionFoodName, getFoodByArea,
} from '../services/RequestDishes';
import RequisitionDrink from '../services/RequestDrinks';

function RecipesAppProvider({ children }) {
  const [dishesResquest, setDishesRequest] = useState({ type: '', description: '' });
  const [drinksResquest, setDrinksRequest] = useState({ type: '', description: '' });

  const [dishes, setDishes] = useState([]);
  const [drinks, setDrinks] = useState([]);

  useEffect(() => {
    if (dishesResquest.description === '') {
      RequisitionFood('').then((response) => setDishes(response));
    }
    if (dishesResquest.type === 'ingrediente') {
      RequisitionFood(dishesResquest.description).then((response) => setDishes(response));
    }
    if (dishesResquest.description === 'area') {
      getFoodByArea(dishesResquest.type).then((response) => setDishes(response));
    }
    if (dishesResquest.type === 'nome') {
      RequisitionFoodName(dishesResquest.description)
        .then((response) => setDishes(response));
    }
    if (dishesResquest.type === 'primeira-letra'
      && dishesResquest.description.length > 1) {
      global.alert('Sua busca deve conter somente 1 (um) caracter');
    } else if (dishesResquest.type === 'primeira-letra' && dishesResquest.description) {
      RequisitionFoodFirstName(dishesResquest.description)
        .then((response) => setDishes(response));
    }
  }, [dishesResquest.type, dishesResquest.description]);

  useEffect(() => {
    RequisitionIdFood(ingredientId).then((response) => setIdDishes(response));
  }, [ingredientId]);

  useEffect(() => {
    if (drinksResquest.description === '') {
      RequisitionDrink('').then((response) => setDrinks(response));
    }
  }, [drinksResquest.type, drinksResquest.description]);

  return (
    <RecipesAppContext.Provider
      value={ {
        dishesResquest,
        setDishesRequest,
        drinksResquest,
        setDrinksRequest,
        dishes,
        drinks } }
    >
      { children }
    </RecipesAppContext.Provider>
  );
}

RecipesAppProvider.propTypes = {
  children: PropTypes.node.isRequired,
};

export default RecipesAppProvider;
